// Configuration for RAMEN Launchpad
// These values will be replaced at build time from .env file
// DO NOT edit this file directly - update .env instead

window.CONFIG = {
  // TMDB API Key - Get from https://www.themoviedb.org/settings/api
  TMDB_API_KEY: '__TMDB_API_KEY__',
  
  // PrivateHD PID - Your personal tracker ID
  PRIVATEHD_PID: '__PRIVATEHD_PID__'
};

// Validation
if (window.CONFIG.TMDB_API_KEY === '__TMDB_API_KEY__') {
  console.warn('⚠️ TMDB_API_KEY not configured. TMDB image features will not work.');
}
if (window.CONFIG.PRIVATEHD_PID === '__PRIVATEHD_PID__') {
  console.warn('⚠️ PRIVATEHD_PID not configured. PrivateHD features will not work.');
}